const options = {
  url: `${process.env.API_BASE}/user/repos`,
  method: 'GET',
  headers: {
    'Accept': 'application/json',
    'Authorization': `Bearer ${bundle.authData.access_token}`
  },
  params: {
    ...bundle.inputData,
    page: bundle.meta.page + 1,
    per_page: 50,
  }
};

return z.request(options)
  .then((response) => {
    response.throwForStatus();
    const results = z.JSON.parse(response.content);

    // You can do any parsing you need for results here before returning them

    return response.json;
  });

const { reviewers } = bundle.inputData;
const options = {
  url: `${process.env.API_BASE}/repos/${bundle.inputData.repo_full_name}/pulls/${bundle.inputData.pr_number}/requested_reviewers`,
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
    'Authorization': `Bearer ${bundle.authData.access_token}`
  },
  body: {
    reviewers: typeof reviewers === 'string' ? reviewers.split(',') : reviewers
  },
};

return z.request(options)
  .then((response) => {
    response.throwForStatus();
    const results = z.JSON.parse(response.content);

    // You can do any parsing you need for results here before returning them

    return results;
  });
